
public class C2_UD04_03_App {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x = 10, y = 40;
		double n = 17.5, m = 24.3;
		
		System.out.println("Valor de X: " + x);
		System.out.println("Valor de Y: " + y);
		System.out.println("Valor de N: " + n);
		System.out.println("Valor de M: " + m + "\n");
		
		System.out.println("Suma de X e Y = " + (x+y));
		System.out.println("Resta de de X e Y = " + (x-y));
		System.out.println("Producto de de X e Y = " + (x*y));
		System.out.println("Cociente de de X e Y = " + (x/y));
		System.out.println("Resto de de X e Y = " + (x%y) + "\n");
		
		System.out.println("Suma de N y M = " + (n+m));
		System.out.println("Resta de de N y M = " + (n-m));
		System.out.println("Producto de de N y M = " + (n*m));
		System.out.println("Cociente de de N y M = " + (n/m));
		System.out.println("Resto de de N y M = " + (n%m) + "\n");
		
		System.out.println("Suma de X y N = " + (x+n));
		
		System.out.println("Cociente de Y y M = " + (y/m));
		System.out.println("Resto de Y y M = " + (y%m) + "\n");
		
		System.out.println("El doble de X: " + 2*x);
		System.out.println("El doble de Y: " + 2*y);
		System.out.println("El doble de N: " + 2*n);
		System.out.println("El doble de M: " + 2*m + "\n");
		
		System.out.println("Suma de todas las variables = " + (x+y+n+m) + "\n");

		System.out.println("Producto de todas las variables = " + x*y*n*m);
		
	}

}
